class MyKingKongPnl extends JPanel{
	Image img;
	public MyKingKongPnl(){
		 img = Toolkit.getDefaultToolkit().getImage("BackGround/back2.png");
	}
	@Override
	public void paint(Graphics g) {
		// TODO Auto-generated method stub
		super.paint(g);
		g.drawImage(img, 20, 0, this);
	}
}
class EnemyMove1 extends Thread{
	KingKongMain main;
	JButton btn;
	int x;
	int y;
	int i = 17;
	int j = 4;
	boolean left;
	int downLevel = 5;
	boolean isLive = true;
	public EnemyMove1(KingKongMain main) {
		this.main = main;
		btn = new JButton();
		btn.setBackground(Color.yellow);
		btn.setBounds(x,y,20,20);
		main.mainPnl.add(btn);
		setEnemyLocation(i,j);
	}
	//적이 움직이는 좌표는 다시 설정해야됨
	@Override
	public void run() {
		// TODO Auto-generated method stub
		while (isLive) {
			try {
				Thread.sleep(100);
			} catch (InterruptedException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
			if(x<700&&x>0&&!left){
				j++;
				setEnemyLocation(i,j);
			}else if(x<700&&x>0&&left){
				x-=10;
				y+=1;
			}else if(x>=700){
				if(downLevel==0){
					x-=10;
					downLevel = 5;
					left = true;
				}else{
					y+=10;
					downLevel--;
				}
			}else if(x<=0){
				if(downLevel==0){
					x+=10;
					downLevel = 5;
					left = false;
				}else{
					y+=10;
					downLevel--;
				}
			}
			btn.setLocation(x,y);
			main.mainPnl.repaint();
		}
	}
	void setEnemyLocation(int i, int j){
		x = (int)(j*8.5)+20;
		y = i*8-20;
		btn.setLocation(x,y);
	}
}
