	int playerI = 0;
	int playerJ = 0;
	public KingKongMain(){
		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
		setSize(750,790);
		getPlayerLocation();
		
		makeBack();
		new EnemyMove1(this).start();
		setVisible(true);
	}
	void makeBack(){
		
		mainPnl = new MyKingKongPnl();
		mainPnl.setLayout(null);
		mainPnl.setBackground(Color.black);
		mainPnl.addKeyListener(this);
		mainPnl.setFocusable(true);
		mainPnl.requestFocus();
		
		
		btn = new JButton();
		btn.setBackground(Color.RED);
		btn.setSize(20,20);
		setLocationPlayer(playerI,playerJ);
		mainPnl.add(btn);
		add(mainPnl);
	}
	void setLocationPlayer(int i,int j){
			btn.setLocation((int)(j*8.5)+20,i*8-20);
			repaint();
			System.out.println(map[i][j]);
	}
	void setIconSize(String addr, JLabel lbl){
		ImageIcon icon = new ImageIcon(addr);
		Image img = icon.getImage();
		img = img.getScaledInstance(750, 770, Image.SCALE_DEFAULT);
		icon = new ImageIcon(img);
		lbl.setIcon(icon);
	}
	public static void main(String[] args) {
		new KingKongMain();
	}
	void getPlayerLocation(){
		for(int i=0;i<map.length;i++){
			for(int j=0;j<map[0].length;j++){
				if(map[i][j]==2){
					playerI = i;
					playerJ = j;
				}
			}
		}
	}
	@Override
	public void keyPressed(KeyEvent e) {
		// TODO Auto-generated method stub
		if(e.getKeyCode()==KeyEvent.VK_RIGHT){
			if(playerJ+1<map[0].length){
				if(map[playerI][playerJ]==4){
					if(map[playerI][playerJ+1]==0){
						if(map[playerI+1][playerJ+1]==1||map[playerI+1][playerJ+1]==3){	//밑으로 떨어지지 않는 경우
							playerJ +=1;
							map[playerI][playerJ-1] = 3;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}else if(map[playerI+1][playerJ+1]==0){	//떨어지는 경우
							playerJ +=1;
							playerI +=1;
							map[playerI-1][playerJ-1] = 3;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}
					}else if(map[playerI][playerJ+1]==3){	//사다리로 직진
						playerJ +=1;
						map[playerI][playerJ-1] = 3;
						map[playerI][playerJ] = 4;
						setLocationPlayer(playerI,playerJ);
					}else{	//위로 올라가는 경우
						playerJ +=1;
						playerI -=1;
						map[playerI+1][playerJ-1] = 3;
						map[playerI][playerJ] = 2;
						setLocationPlayer(playerI,playerJ);
					}
				}else{
					if(map[playerI][playerJ+1]==0){
						if(map[playerI+1][playerJ+1]==1||map[playerI+1][playerJ+1]==3){
							playerJ +=1;
							map[playerI][playerJ-1] = 0;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}else{
							playerJ +=1;
							playerI +=1;
							map[playerI-1][playerJ-1] = 0;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}
					}else if(map[playerI][playerJ+1]==3){
						playerJ +=1;
						map[playerI][playerJ-1] = 0;
						map[playerI][playerJ] = 4;
						setLocationPlayer(playerI,playerJ);
					}else{
						playerJ +=1;
						playerI -=1;
						map[playerI+1][playerJ-1] = 0;
						map[playerI][playerJ] = 2;
						setLocationPlayer(playerI,playerJ);
					}
				}
				
			}
		}else if(e.getKeyCode()==KeyEvent.VK_LEFT){
			if(playerJ>0){
				if(map[playerI][playerJ]==4){	//사다리 위치에 플레이어가 위치하고 있었던 경우 4로 표현. 4는 플레이어가 벗어나는 경우 다시 3으로 변경
					if(map[playerI][playerJ-1]==0){
						if(map[playerI+1][playerJ-1]==1||map[playerI+1][playerJ-1]==3){
							playerJ -=1;
							map[playerI][playerJ+1] = 3;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}else{
							playerJ -=1;
							playerI +=1;
							map[playerI-1][playerJ+1] = 3;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}
					}else if(map[playerI][playerJ-1]==3){
							playerJ -=1;
							map[playerI][playerJ+1] = 3;
							map[playerI][playerJ] = 4;
							setLocationPlayer(playerI,playerJ);
					}else{
						playerJ -=1;
						playerI -=1;
						map[playerI+1][playerJ+1] = 3;
						map[playerI][playerJ] = 2;
						setLocationPlayer(playerI,playerJ);
					}
				}else{	//플레이어가 2인 경우. 4가 아니라
					if(map[playerI][playerJ-1]==0){
						if(map[playerI+1][playerJ-1]==1||map[playerI+1][playerJ-1]==3){
							playerJ -=1;
							map[playerI][playerJ+1] = 0;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}else{
							playerJ -=1;
							playerI +=1;
							map[playerI-1][playerJ+1] = 0;
							map[playerI][playerJ] = 2;
							setLocationPlayer(playerI,playerJ);
						}
					}else if(map[playerI][playerJ-1]==3){
							playerJ -=1;
							map[playerI][playerJ+1] = 0;
							map[playerI][playerJ] = 4;
							setLocationPlayer(playerI,playerJ);
					}else{
						playerJ -=1;
						playerI -=1;
						map[playerI+1][playerJ+1] = 0;
						map[playerI][playerJ] = 2;
						setLocationPlayer(playerI,playerJ);
					}
					
				}
				
			}
		}else if(e.getKeyCode()==KeyEvent.VK_UP){
			if(map[playerI][playerJ]==4){
				if(map[playerI-1][playerJ]==0){
					playerI -=1;
					map[playerI+1][playerJ] =3;
					map[playerI][playerJ] =2;
					setLocationPlayer(playerI, playerJ);
				}else{
					playerI -=1;
					map[playerI+1][playerJ] =3;
					map[playerI][playerJ] =4;
					setLocationPlayer(playerI, playerJ);
				}
			}
		}else if(e.getKeyCode()==KeyEvent.VK_DOWN){
			if(map[playerI+1][playerJ]==3){
				if(map[playerI][playerJ]==2){
					playerI +=1;
					map[playerI-1][playerJ] =0;
					map[playerI][playerJ] =4;
					setLocationPlayer(playerI, playerJ);
				}else if(map[playerI][playerJ]==4){
					playerI +=1;
					map[playerI-1][playerJ] =3;
					map[playerI][playerJ] =4;
					setLocationPlayer(playerI, playerJ);
				}
			}
		}
	}
